var i, ur, pox, poy, pcx, pcy, player, pTranslate, obst1, obst2, obst3;

function init() {
  obst1 = new Obstacle(document.getElementById("obst1"));
  obst2 = new Obstacle(document.getElementById("obst2"));
  obst3 = new Obstacle(document.getElementById("obst3"));
  // alert(obst1.r);
  // alert(obst2.r);
  // alert(obst3.r);
}

function Obstacle(svgEquiv) {
  this.svgEquiv = svgEquiv;
  this.r = parseInt(this.svgEquiv.getAttribute("r"));
}

function verify() {
  ur = prompt("Please enter password to continue.");
  if (ur !== "sisp1305g1") {
    alert("You will be returned to the homepage.");
    window.location = "http://donaldkellett.github.io/HKUST-Summer-Institute-SISP-1305";
  } else {
    pox = parseInt(document.getElementById("playerHead").getAttribute("cx"));
    poy = parseInt(document.getElementById("playerHead").getAttribute("cy"));
    player = {
      isJumping: false,
      group: document.getElementById("player"),
      jump: function() {
        this.isJumping = true;
        pTranslate = 0;
        for (i = 0; i < 30; i++) {
          setTimeout(function() {
            pTranslate -= 10;
            player.group.setAttribute("transform", "translate(0 " + pTranslate + ")");
            pcx = pox;
            pcy = poy + pTranslate;
            // alert("("+pcx+","+pcy+")");
          }, 50 * i);
        }
        for (i = 0; i < 30; i++) {
          setTimeout(function() {
            pTranslate += 10;
            player.group.setAttribute("transform", "translate(0 " + pTranslate + ")");
            pcx = pox;
            pcy = poy + pTranslate;
            // alert("("+pcx+","+pcy+")");
          }, 50 * i + 1500);
        }
        setTimeout(function() {
          player.isJumping = false;
        }, 3000);
      }
    };
    init();
  }
}

document.onkeydown = function() {
  if (event.keyCode == 38 && player.isJumping == false) {
    player.jump();
    // alert(event.keyCode);
  }
}
